import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-site-packages',
  templateUrl: './site-packages.component.html',
  styleUrls: ['./site-packages.component.css']
})
export class SitePackagesComponent implements OnInit {

  show_sites = [ false, false ];
  count: number = 0;

  constructor() { }

  ngOnInit(): void {
    
  }

  showMore(){
    this.show_sites[this.count++] = true;
  }

}
